function stdout(e){if(modes.indexOf("stdout")===-1)return;process.stdout.write("stdout "+e+"\n")}function stderr(e){if(modes.indexOf("stderr")===-1)return;process.stderr.write("stderr "+e+"\n")}var exit=require("../../lib/exit"),errorCode=process.argv[2],max=process.argv[3],modes=process.argv.slice(4);for(var i=0;i<max;i++){stdout(i);stderr(i)}exit(errorCode);stdout("fail");stderr("fail");