/**
 * class Namespace
 *
 * Simple object for storing attributes. Implements equality by attribute names
 * and values, and provides a simple string representation.
 *
 * See also [original guide][1]
 *
 * [1]:http://docs.python.org/dev/library/argparse.html#the-namespace-object
 **/"use strict";var _=require("underscore"),Namespace=module.exports=function(t){_.extend(this,t)};Namespace.prototype.isset=function(e){return _.has(this,e)};Namespace.prototype.set=function(e,t){typeof e=="object"?_.extend(this,e):this[e]=t;return this};Namespace.prototype.get=function(e,t){return this[e]?this[e]:t};Namespace.prototype.unset=function(e,t){var n=this[e];if(n!==null){delete this[e];return n}return t};