$(document).ready(function(){module("Utility");test("utility: noConflict",function(){var e=_.noConflict();ok(e.isUndefined(_),"The '_' variable has been returned to its previous state.");var t=e.intersect([-1,0,1,2],[1,2,3,4]);equals(t.join(", "),"1, 2","but the intersection function still works");window._=e});test("utility: identity",function(){var e={name:"moe"};equals(_.identity(e),e,"moe is the same as his identity")});test("utility: uniqueId",function(){var e=[],t=0;while(t++<100)e.push(_.uniqueId());equals(_.uniq(e).length,e.length,"can generate a globally-unique stream of ids")});test("utility: times",function(){var e=[];_.times(3,function(t){e.push(t)});ok(_.isEqual(e,[0,1,2]),"is 0 indexed");e=[];_(3).times(function(t){e.push(t)});ok(_.isEqual(e,[0,1,2]),"works as a wrapper")});test("utility: mixin",function(){_.mixin({myReverse:function(e){return e.split("").reverse().join("")}});equals(_.myReverse("panacea"),"aecanap","mixed in a function to _");equals(_("champ").myReverse(),"pmahc","mixed in a function to the OOP wrapper")});test("utility: _.escape",function(){equals(_.escape("Curly & Moe"),"Curly &amp; Moe");equals(_.escape("Curly &amp; Moe"),"Curly &amp;amp; Moe")});test("utility: template",function(){var e=_.template("<%= thing %> is gettin' on my noives!"),t=e({thing:"This"});equals(t,"This is gettin' on my noives!","can do basic attribute interpolation");var n=_.template("A <% this %> B");equals(n(),"A  B");var r=_.template("<%= thing %> is \\ridanculous");equals(r({thing:"This"}),"This is \\ridanculous");var i=_.template('<%= a ? "checked=\\"checked\\"" : "" %>');equals(i({a:!0}),'checked="checked"',"can handle slash escapes in interpolations.");var s=_.template("<ul><%       for (key in people) {     %><li><%= people[key] %></li><% } %></ul>");t=s({people:{moe:"Moe",larry:"Larry",curly:"Curly"}});equals(t,"<ul><li>Moe</li><li>Larry</li><li>Curly</li></ul>","can run arbitrary javascript in templates");var o=_.template("<ul><% _.each(numbers.split('\\n'), function(item) { %><li><%= item %></li><% }) %></ul>");t=o({numbers:"one\ntwo\nthree\nfour"});equals(t,"<ul><li>one</li><li>two</li><li>three</li><li>four</li></ul>","Can use escaped characters (e.g. \\n) in Javascript");var u=_.template('<%= pageCount %> <%= thumbnails[pageCount] %> <% _.each(thumbnails, function(p) { %><div class="thumbnail" rel="<%= p %>"></div><% }); %>');t=u({pageCount:3,thumbnails:{1:"p1-thumbnail.gif",2:"p2-thumbnail.gif",3:"p3-thumbnail.gif"}});equals(t,'3 p3-thumbnail.gif <div class="thumbnail" rel="p1-thumbnail.gif"></div><div class="thumbnail" rel="p2-thumbnail.gif"></div><div class="thumbnail" rel="p3-thumbnail.gif"></div>');var a=_.template("<div><p>Just some text. Hey, I know this is silly but it aids consistency.</p></div>");t=a();equals(t,"<div><p>Just some text. Hey, I know this is silly but it aids consistency.</p></div>");var f=_.template("It's its, not it's");equals(f({}),"It's its, not it's");var l=_.template("<%      if(foo == 'bar'){     %>Statement quotes and 'quotes'.<% } %>");equals(l({foo:"bar"}),"Statement quotes and 'quotes'.");var c=_.template("This\n		is: <%= x %>.\n	ok.\nend.");equals(c({x:"that"}),"This\n		is: that.\n	ok.\nend.");var h=_.template("<i><%- value %></i>"),t=h({value:"<script>"});equals(t,"<i>&lt;script&gt;</i>");var p={name:"Moe",template:_.template("I'm <%= this.name %>")};equals(p.template(),"I'm Moe");if(!$.browser.msie){var d=_.template($("#template").html());equals(d({data:12345}).replace(/\s/g,""),"<li>24690</li>")}_.templateSettings={evaluate:/\{\{([\s\S]+?)\}\}/g,interpolate:/\{\{=([\s\S]+?)\}\}/g};var v=_.template("<ul>{{ for (key in people) { }}<li>{{= people[key] }}</li>{{ } }}</ul>");t=v({people:{moe:"Moe",larry:"Larry",curly:"Curly"}});equals(t,"<ul><li>Moe</li><li>Larry</li><li>Curly</li></ul>","can run arbitrary javascript in templates");var m=_.template("It's its, not it's");equals(m({}),"It's its, not it's");var l=_.template("{{ if(foo == 'bar'){ }}Statement quotes and 'quotes'.{{ } }}");equals(l({foo:"bar"}),"Statement quotes and 'quotes'.");_.templateSettings={evaluate:/<\?([\s\S]+?)\?>/g,interpolate:/<\?=([\s\S]+?)\?>/g};var g=_.template("<ul><? for (key in people) { ?><li><?= people[key] ?></li><? } ?></ul>");t=g({people:{moe:"Moe",larry:"Larry",curly:"Curly"}});equals(t,"<ul><li>Moe</li><li>Larry</li><li>Curly</li></ul>","can run arbitrary javascript in templates");var y=_.template("It's its, not it's");equals(y({}),"It's its, not it's");var l=_.template("<? if(foo == 'bar'){ ?>Statement quotes and 'quotes'.<? } ?>");equals(l({foo:"bar"}),"Statement quotes and 'quotes'.");_.templateSettings={interpolate:/\{\{(.+?)\}\}/g};var b=_.template("Hello {{planet}}!");equals(b({planet:"World"}),"Hello World!","can mimic mustache.js");var w=_.template("a null undefined {{planet}}");equals(w({planet:"world"}),"a null undefined world","can handle missing escape and evaluate settings")})});